import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: 'app.component.html',
})
export class AppComponent {
  titulo: string = '02-contador-app';
  numero: number = 10;

  sumar() {
    this.numero += 1
  }

}
<!-- 
  Habitualmente, evitaremos el uso de lógica en la vista (mvc) 

  <h1>{{ titulo }}</h1>
  <br>
  <button (click) = "numero = numero + 1">+1</button>
  <span> {{ numero }}</span>
  <button (click) = "numero = numero - 1">-1</button>
-->

<h1>{{ titulo }}</h1>
<br>
<button (click) = "numero = numero + 1">+1</button>
<span> {{ numero }}</span>
<button (click) = "numero = numero - 1">-1</button>
