import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: 'app.component.html',
})
export class AppComponent {
  titulo: string = '02-contador-app';
  numero: number = 10;
  base: number = 5;

  /* 
    Para simplificar la lectura, optimizaremos el código,
      por ello, el sigueinte código no lo vamos a utilizar (es correcto)
  */
  sumar() {
    this.numero += 1
  }

  restar() {
    this.numero -= 1
  }

  // Óprimo:
  acumular(valor: number) {
    this.numero += valor;
  }
}
<!-- 
  Habitualmente, evitaremos el uso de lógica en la vista (mvc) 

  <h1>{{ titulo }}</h1>
  <br>
  <button (click) = "numero = numero + 1">+1</button>
  <span> {{ numero }}</span>
  <button (click) = "numero = numero - 1">-1</button>
-->

<!--
  En este caso, cada vez que presionamos los correspondientes botones,
    se incrementa o decrementa el numero correspondien
-->
<h1>{{ titulo }}</h1>
<br>
<button (click) = "acumular(+1)">+ {{ base }}</button>
<span> {{ numero }}</span>
<button (click) = "acumular(-1)">-1</button>
